#include "testcase.h"
#include "interface.h"

bool checkRst(int *rst, int rstSize, int *excp, int excpSize)
{
	int idx_rst;
	int idx_excp;
	if (rstSize != excpSize) {
		return FALSE;
	}

	for (idx_rst = 0; idx_rst < rstSize; idx_rst++) {
		for (idx_excp = 0; idx_excp < excpSize; idx_excp++) {
			if (rst[idx_rst] == excp[idx_excp]) {
				break;
			}
		}
		if (idx_excp == excpSize) {
			return FALSE;
		}
	}

	for (idx_excp = 0; idx_excp < excpSize; idx_excp++) {
		for (idx_rst = 0; idx_rst < rstSize; idx_rst++) {
			if (rst[idx_rst] == excp[idx_excp]) {
				break;
			}
		}
		if (idx_rst == rstSize) {
			return FALSE;
		}
	}
	return TRUE;
}

void testcase1(void)
{
	char *s = "barfoothefoobarman";
	char *words[] = {"foo","bar"};
	int returnSize = 0;
	int *rst = findSubstring(s, words, ARRAY_SIZE(words), &returnSize);
	int excp[] = {0, 9};
	bool flag = checkRst(rst, returnSize, excp, ARRAY_SIZE(excp));
    display_result(flag, __FUNCTION__);
}

